		invalidLeadByte[0x85] = true;
		invalidLeadByte[0x86] = true;
		invalidLeadByte[0xEB] = true;
		invalidLeadByte[0xEC] = true;
		invalidLeadByte[0xEF] = true;
		invalidLeadByte[0xFA] = true;
